#!/bin/sh
# Admin user account admin_user='mac_sys_admin'
# Check if ARD_AllLocalUsers is set, if not, install if [ ! $(defaults read /Library/Preferences/com.apple.RemoteManagement ARD_AllLocalUsers) ]; then exit 0; fi # Check if ARD_AllLocalUsers is zero, if not, install if [ $(defaults read /Library/Preferences/com.apple.RemoteManagement ARD_AllLocalUsers) != 0 ]; then exit 0; fi # Check if correct naprivs exist in macsys, if not, install if [ "$(dscl . read /Users/$admin_user naprivs)" != 'dsAttrTypeNative:naprivs: -2147483395' ]; then exit 0; fi # All is fine, no install necessary exit 1;
